def path(value: str):
    file = value.split("/")[-1].encode("utf-8")
    path = value.replace(value.split("/")[-1], "").encode("utf-8")
    return len(path).to_bytes(4, "big") + path + len(file).to_bytes(4, "big") + file + zlib.crc32(value.encode("utf-8")).to_bytes(4, "big")


def string(value: str):
    return len(value).to_bytes(4, "big") + value.encode("utf-8")


def integer(value: int):
    return value.to_bytes(4, "big")


